	// Indicates that the supplied operation (method name) is to be performed.
	void performOperation(Object resource, String operation, Object[] args);
	// Rollback all recorded operations by performing each of the recorded rollback operations.
	void rollback();
}

public interface ConditionCriteria {
	// Appends an GreaterThanOrEqualsFilter.
	ContainerCriteria gte(String value);
	// Appends an EqualsFilter.
	ContainerCriteria is(String value);
	// Appends a PresentFilter.
	ContainerCriteria isPresent();
	// Appends a LikeFilter.
	ContainerCriteria like(String value);
	// Appends a LessThanOrEqualsFilter.
	ContainerCriteria lte(String value);
	// Negates the currently constructed operation.
	ConditionCriteria not();
}

public interface ContainerCriteria extends LdapQuery {
	// Append a logical And condition to the currently built filter.
	ConditionCriteria and(String attribute);
	// Append an And condition for a nested criterion.
	ContainerCriteria and(ContainerCriteria nested);
	// Append a logical Or condition to the currently built filter.
	ConditionCriteria or(String attribute);
	// Append an Or condition for a nested criterion.
	ContainerCriteria or(ContainerCriteria nested);
}

